import './App.css';
import axios from "axios";
import React, { useState } from 'react';

function App() {

  const[file, setFile] = useState();
  const[tags, setTags] = useState([]);

  function handleChange(event){
    setFile(event.target.files[0])
  }

  function checkChange(event){
    console.log(event.target.value)
    setTags.append(event.target.value)
  }

  function handleSubmit(event){
    event.preventDefault();

    // Creation of form data
    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);

    const config = {
      headers:{
        'content-type': 'multipart/form-data',
      },
    };

    console.log(tags)

    // Posting to flask
    axios.post('/upload', formData, config).then((response) => {
      console.log(response.data);
    });
  }

  return (
    <div className="App">
          <title>CCIoT GUI</title>
          <form method="POST" onSubmit={handleSubmit}>
              {/* Image upload */}
              <h1>File Upload</h1>
              <input type="file" onChange={handleChange}/>

              {/* Tags */}
              <h2>Select the appropriate tags</h2>
                <input type="checkbox" id="tags" name="tags" value="male" onChange={this.checkChange}/>
                <input type="checkbox" id="tags" name="tags" value="female" onChange={this.checkChange}/>
                <input type="checkbox" id="tags" name="tags" value="beard" onChange={checkChange}/>
                <input type="checkbox" id="tags" name="tags" value="happy" onChange={checkChange}/>
              <button type='submit'>Upload</button>
          </form>
    </div>
  );
}

export default App;
