              <input type="checkbox" id="beard" name="beard" value="beard">beard</input>
              <input type="checkbox" id="happy" name="happy" value="happy">happy</input> */}
            <button type='submit'>Upload</button>
          </form>
    </div>
  );
}

export default App;
